define([], function () {
    var generateRandomSolution = function(solutionSize) {
        return _.map(_.range(solutionSize), function (garbage) {
            return Math.random();
        });
    };


    var bogusSolver = function(problem) {
        this.problem = problem;

        this.step = function() {
            return _.map(_.range(10), function (garbage) {
                return generateRandomSolution(problem.getSolutionSize());
            });
        };
    };

    return bogusSolver;
});
