define([], function() {
    /* Solvers list
     *   If you make a new solver, put it here.
     */
    var solvers = [
		{
			userName: "Ant",
			fileName: "AntSolver"
		},
		{
			userName: "Flock",
			fileName: "FlockSolver"
		},
		{
			userName: "FlockPred",
			fileName: "FlockPredSolver"
		},
		{
			userName:"Iterative",
			fileName: "IterSolver"
		},
		{
			userName: "Genetic",
			fileName: "GaSolver"
		},
		{	userName: "Amoeba",
			fileName: "AmoebaSolver"
		},
		{	userName: "Bee",
			fileName: "BeeSolver"
		},
		{	userName: "Frog",
			fileName: "FrogSolver"
		},
		{
			userName: "Annealing",
			fileName: "SaSolver"
		},
		{
			userName: "Wolf",
			fileName: "WolfSolver"
      },
		{
			userName: "Cuckoo",
			fileName: "CuckooSolver"
		},
		{
			userName: "Rat",
			fileName: "RatSolver"
		},
		{
			userName: "Dolphin",
			fileName: "DolphinSolver"
		}
    ];

	 // Add path information to solvers

	 solvers = _.map(solvers, function(solverData) {
		 return {
			 userName: solverData.userName,
				fileName: "solvers/" + solverData.fileName
		 }
	 });

	 var loadSolvers = function(callback) {
		 var solverFiles = _.map(solvers, function(solverData) {
			 return solverData.fileName;
		 });
		 //require(solverFiles, function() {
		 var solverDict = {};
		 var solverObjs = solverFiles.map(function(f) { return require(f); });
		 for (var i = 0; i < solvers.length; i++) {
			 solverDict[solvers[i].userName] = solverObjs[i];
		 }
		 callback(solverDict);
		 //});
	 };
	 return {
		 loadSolvers: loadSolvers
	 }

		 
});
